/**
 * 加载"css"文件
 */
declare module '*.css' {
  const content: any;
  export default content;
}

/**
 * 加载"less"文件
 */
declare module '*.less' {
  const content: any;
  export default content;
}

/**
 * 加载"sass"文件
 */
declare module '*.sass' {
  const content: any;
  export default content;
}

/**
 * 加载"scss"文件
 */
declare module '*.scss' {
  const content: any;
  export default content;
}

/**
 * 加载"json"文件
 */
declare module '*.json' {
  const content: any;
  export default content;
}
